export default function offerCard({amount:n,license_name:i,name_prod:s,price_key:c,is_best:a,link:e}){return`<li class="offers__card">\n      <div class="price">\n        ${a?'<div class="price__best">Best Value</div>':""}\n        ${c?.includes("%")?`<div class="price__discount">\n      <div class="price__discount-ribbon">\n        <div class="price__discount-ribbon-wrapper">\n          <div class="price__discount-text">\n            <p class="price__discount-amount">${c}</p>\n            <p class="price__discount-off">OFF</p>\n          </div>\n        </div>\n      </div>\n    </div>`:""}\n        <div class="price__main">\n          <p class="price__main-amount">$${n}</p>\n          ${c?.includes("%")?`<p class="price__main-original">\n          $${+n*(100/c.replace(/%/g,""))} \n          </p>`:""}\n        </div>\n      </div>\n      <div class="description">\n        <p>${s}</p>\n        <p class="description__license">${i}</p>\n      </div>\n      <a href="${e}" target="_blank" class="download">\n        <span class="download__text">Download</span>\n        <img\n          src="./img/download.png"\n          alt="Download"\n          height="30"\n          width="30"\n        />\n      </a>\n    </li>`}